import React, { useState } from 'react';
import { useHistory } from 'react-router-dom';

const CreateEditResume = () => {
  const history = useHistory();

  const [name, setName] = useState('');
  const [email, setEmail] = useState('');
  const [address, setAddress] = useState('');
  const [phone, setPhone] = useState('');
  const [education, setEducation] = useState([{ institute: '', year: '', degree: '' }]);
  const [experience, setExperience] = useState([{ company: '', year: '', designation: '' }]);
  const [skills, setSkills] = useState('');

  const handleEducationChange = (index, field, value) => {
    const updatedEducation = [...education];
    updatedEducation[index][field] = value;
    setEducation(updatedEducation);
  };

  const handleAddEducation = () => {
    setEducation([...education, { institute: '', year: '', degree: '' }]);
  };

  const handleExperienceChange = (index, field, value) => {
    const updatedExperience = [...experience];
    updatedExperience[index][field] = value;
    setExperience(updatedExperience);
  };

  const handleAddExperience = () => {
    setExperience([...experience, { company: '', year: '', designation: '' }]);
  };

  const handleSkillChange = (e) => {
    setSkills(e.target.value);
  };

  const handleSubmit = (e) => {
    e.preventDefault();

    // Create the resume object
    const resume = {
      name,
      email,
      address,
      phone,
      education,
      experience,
      skills: skills.split(',').map((skill) => skill.trim()),
    };

    // Store the resume data in localStorage or send it to a server
    localStorage.setItem('resume', JSON.stringify(resume));

    // Redirect to view resume page
    history.push('/view-resume');
  };

  return (
    <div>
      <h2>Create/Edit Resume</h2>

      <form onSubmit={handleSubmit}>
        {/* Form fields for name, email, address, and phone */}
        
        {/* Education section */}
        
        {/* Experience section */}
        
        {/* Skills section */}

        <button type="submit" className="btn btn-primary">Save Resume</button>
      </form>
    </div>
  );
};

export default CreateEditResume;
