import React, { useState } from 'react';

const CreateEditResume = () => {
  const [name, setName] = useState('');
  const [email, setEmail] = useState('');
  const [address, setAddress] = useState('');
  const [phone, setPhone] = useState('');
  const [education, setEducation] = useState([{ institute: '', year: '', degree: '' }]);
  const [experience, setExperience] = useState([{ company: '', year: '', designation: '' }]);
  const [skills, setSkills] = useState([]);
  const [newSkill, setNewSkill] = useState('');

  const handleEducationChange = (index, event) => {
    const { name, value } = event.target;
    const updatedEducation = [...education];
    updatedEducation[index][name] = value;
    setEducation(updatedEducation);
  };

  const handleExperienceChange = (index, event) => {
    const { name, value } = event.target;
    const updatedExperience = [...experience];
    updatedExperience[index][name] = value;
    setExperience(updatedExperience);
  };

  const handleSkillChange = (event) => {
    setNewSkill(event.target.value);
  };

  const handleAddEducation = () => {
    setEducation([...education, { institute: '', year: '', degree: '' }]);
  };

  const handleAddExperience = () => {
    setExperience([...experience, { company: '', year: '', designation: '' }]);
  };

  const handleAddSkill = () => {
    if (newSkill.trim() !== '') {
      setSkills([...skills, newSkill.trim()]);
      setNewSkill('');
    }
  };

  const handleSaveResume = () => {
    // Implement the logic to save the resume data
  };

  return (
    <div className="container">
      <h2>Create/Edit Resume</h2>
      <form>
        <div className="form-group">
          <label htmlFor="name">Name:</label>
          <input
            type="text"
            id="name"
            value={name}
            onChange={(e) => setName(e.target.value)}
          />
        </div>
        <div className="form-group">
          <label htmlFor="email">Email:</label>
          <input
            type="email"
            id="email"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
          />
        </div>
        <div className="form-group">
          <label htmlFor="address">Address:</label>
          <input
            type="text"
            id="address"
            value={address}
            onChange={(e) => setAddress(e.target.value)}
          />
        </div>
        <div className="form-group">
          <label htmlFor="phone">Phone:</label>
          <input
            type="text"
            id="phone"
            value={phone}
            onChange={(e) => setPhone(e.target.value)}
          />
        </div>
        <div className="form-group">
          <label>Education:</label>
          {education.map((edu, index) => (
            <div key={index} className="mb-3">
              <input
                type="text"
                name="institute"
                placeholder="Institute"
                value={edu.institute}
                onChange={(e) => handleEducationChange(index, e)}
              />
              <input
                type="text"
                name="year"
                placeholder="Year"
                value={edu.year}
                onChange={(e) => handleEducationChange(index, e)}
              />
              <input
                type="text"
                name="degree"
                placeholder="Degree"
                value={edu.degree}
                onChange={(e) => handleEducationChange(index, e)}
              />
            </div>
          ))}
          <button type="button" onClick={handleAddEducation}>
            Add Education
          </button>
        </div>
        <div className="form-group">
          <label>Experience:</label>
          {experience.map((exp, index) => (
            <div key={index} className="mb-3">
              <input
                type="text"
                name="company"
                placeholder="Company"
                value={exp.company}
                onChange={(e) => handleExperienceChange(index, e)}
              />
              <input
                type="text"
                name="year"
                placeholder="Year"
                value={exp.year}
                onChange={(e) => handleExperienceChange(index, e)}
              />
              <input
                type="text"
                name="designation"
                placeholder="Designation"
                value={exp.designation}
                onChange={(e) => handleExperienceChange(index, e)}
              />
            </div>
          ))}
          <button type="button" onClick={handleAddExperience}>
            Add Experience
          </button>
        </div>
        <div className="form-group">
          <label htmlFor="skills">Skills:</label>
          <div className="input-group mb-3">
            <input
              type="text"
              id="skills"
              value={newSkill}
              onChange={handleSkillChange}
            />
            <button type="button" onClick={handleAddSkill}>
              Add Skill
            </button>
          </div>
          <ul>
            {skills.map((skill, index) => (
              <li key={index}>{skill}</li>
            ))}
          </ul>
        </div>
        <button type="button" onClick={handleSaveResume}>
          Save Resume
        </button>
      </form>
    </div>
  );
};

export default CreateEditResume;
