import React, { useState } from 'react';
import { useHistory } from 'react-router-dom';

const CreateEditResume = () => {
  const history = useHistory();

  const [name, setName] = useState('');
  const [email, setEmail] = useState('');
  const [address, setAddress] = useState('');
  const [phone, setPhone] = useState('');
  const [education, setEducation] = useState([{ institute: '', year: '', degree: '' }]);
  const [experience, setExperience] = useState([{ company: '', year: '', designation: '' }]);
  const [skills, setSkills] = useState('');

  const handleEducationChange = (index, field, value) => {
    const updatedEducation = [...education];
    updatedEducation[index][field] = value;
    setEducation(updatedEducation);
  };

  const handleAddEducation = () => {
    setEducation([...education, { institute: '', year: '', degree: '' }]);
  };

  const handleExperienceChange = (index, field, value) => {
    const updatedExperience = [...experience];
    updatedExperience[index][field] = value;
    setExperience(updatedExperience);
  };

  const handleAddExperience = () => {
    setExperience([...experience, { company: '', year: '', designation: '' }]);
  };

  const handleSkillChange = (e) => {
    setSkills(e.target.value);
  };

  const handleSubmit = (e) => {
    e.preventDefault();

    // Create the resume object
    const resume = {
      name,
      email,
      address,
      phone,
      education,
      experience,
      skills: skills.split(',').map((skill) => skill.trim()),
    };

    // Do something with the resume data (e.g., send it to a server)

    // Redirect to view resume page and pass the resume data as state
    history.push({
      pathname: '/view-resume',
      state: { resume },
    });
  };

  return (
    <div>
      <h2>Create/Edit Resume</h2>

      <form onSubmit={handleSubmit}>
        <div className="form-group">
          <label>Name:</label>
          <input
            type="text"
            className="form-control"
            value={name}
            onChange={(e) => setName(e.target.value)}
            required
          />
        </div>

        <div className="form-group">
          <label>Email:</label>
          <input
            type="email"
            className="form-control"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
            required
          />
        </div>

        <div className="form-group">
          <label>Address:</label>
          <input
            type="text"
            className="form-control"
            value={address}
            onChange={(e) => setAddress(e.target.value)}
            required
          />
        </div>

        <div className="form-group">
          <label>Phone:</label>
          <input
            type="tel"
            className="form-control"
            value={phone}
            onChange={(e) => setPhone(e.target.value)}
            required
          />
        </div>

        <div className="form-group">
          <label>Education:</label>
          {education.map((edu, index) => (
            <div key={index}>
              <input
                type="text"
                className="form-control"
                placeholder="Institute"
                value={edu.institute}
                onChange={(e) => handleEducationChange(index, 'institute', e.target.value)}
                required
              />

              <input
                type="text"
                className="form-control"
                placeholder="Year"
                value={edu.year}
                onChange={(e) => handleEducationChange(index, 'year', e.target.value)}
                required
              />

              <input
                type="text"
                className="form-control"
                placeholder="Degree"
                value={edu.degree}
                onChange={(e) => handleEducationChange(index, 'degree', e.target.value)}
                required
              />
            </div>
          ))}
          <button type="button" className="btn btn-secondary" onClick={handleAddEducation}>
            Add Education
          </button>
        </div>

        <div className="form-group">
          <label>Experience:</label>
          {experience.map((exp, index) => (
            <div key={index}>
              <input
                type="text"
                className="form-control"
                placeholder="Company"
                value={exp.company}
                onChange={(e) => handleExperienceChange(index, 'company', e.target.value)}
                required
              />

              <input
                type="text"
                className="form-control"
                placeholder="Year"
                value={exp.year}
                onChange={(e) => handleExperienceChange(index, 'year', e.target.value)}
                required
              />

              <input
                type="text"
                className="form-control"
                placeholder="Designation"
                value={exp.designation}
                onChange={(e) => handleExperienceChange(index, 'designation', e.target.value)}
                required
              />
            </div>
          ))}
          <button type="button" className="btn btn-secondary" onClick={handleAddExperience}>
            Add Experience
          </button>
        </div>

        <div className="form-group">
          <label>Skills:</label>
          <input
            type="text"
            className="form-control"
            value={skills}
            onChange={handleSkillChange}
            required
          />
        </div>

        <button type="submit" className="btn btn-primary">
          Save Resume
        </button>
      </form>
    </div>
  );
};

export default CreateEditResume;
