import React, { useState } from 'react';

const CreateEditResume = () => {
  const [resume, setResume] = useState({
    name: '',
    email: '',
    address: '',
    phone: '',
    education: [{ institute: '', year: '', degree: '' }],
    experience: [{ company: '', year: '', designation: '' }],
    skills: [],
  });

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setResume((prevState) => ({
      ...prevState,
      [name]: value,
    }));
  };

  const handleEducationChange = (index, field, value) => {
    const updatedEducation = [...resume.education];
    updatedEducation[index][field] = value;
    setResume((prevState) => ({
      ...prevState,
      education: updatedEducation,
    }));
  };

  const handleAddEducation = () => {
    setResume((prevState) => ({
      ...prevState,
      education: [...prevState.education, { institute: '', year: '', degree: '' }],
    }));
  };

  const handleExperienceChange = (index, field, value) => {
    const updatedExperience = [...resume.experience];
    updatedExperience[index][field] = value;
    setResume((prevState) => ({
      ...prevState,
      experience: updatedExperience,
    }));
  };

  const handleAddExperience = () => {
    setResume((prevState) => ({
      ...prevState,
      experience: [...prevState.experience, { company: '', year: '', designation: '' }],
    }));
  };

  const handleSkillChange = (e) => {
    const skills = e.target.value.split(',');
    setResume((prevState) => ({
      ...prevState,
      skills,
    }));
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    console.log(resume);
  };

  return (
    <div className="container">
      <h2 className="my-4">Create/Edit Resume</h2>
      <form onSubmit={handleSubmit}>
        <div className="form-group">
          <label htmlFor="name">Name:</label>
          <input
            type="text"
            className="form-control"
            id="name"
            name="name"
            value={resume.name}
            onChange={handleInputChange}
            required
          />
        </div>

        <div className="form-group">
          <label htmlFor="email">Email:</label>
          <input
            type="email"
            className="form-control"
            id="email"
            name="email"
            value={resume.email}
            onChange={handleInputChange}
            required
          />
        </div>

        <div className="form-group">
          <label htmlFor="address">Address:</label>
          <input
            type="text"
            className="form-control"
            id="address"
            name="address"
            value={resume.address}
            onChange={handleInputChange}
            required
          />
        </div>

        <div className="form-group">
          <label htmlFor="phone">Phone:</label>
          <input
            type="text"
            className="form-control"
            id="phone"
            name="phone"
            value={resume.phone}
            onChange={handleInputChange}
            required
          />
        </div>

        <div className="form-group">
          <label>Education:</label>
          {resume.education.map((edu, index) => (
            <div key={index} className="mb-3">
              <input
                type="text"
                className="form-control"
                placeholder="Institute"
                value={edu.institute}
                onChange={(e) => handleEducationChange(index, 'institute', e.target.value)}
                required
              />

              <input
                type="text"
                className="form-control"
                placeholder="Year"
                value={edu.year}
                onChange={(e) => handleEducationChange(index, 'year', e.target.value)}
                required
              />

              <input
                type="text"
                className="form-control"
                placeholder="Degree"
                value={edu.degree}
                onChange={(e) => handleEducationChange(index, 'degree', e.target.value)}
                required
              />
            </div>
          ))}
          <button type="button" className="btn btn-primary" onClick={handleAddEducation}>
            Add Education
          </button>
        </div>

        <div className="form-group">
          <label>Experience:</label>
          {resume.experience.map((exp, index) => (
            <div key={index} className="mb-3">
              <input
                type="text"
                className="form-control"
                placeholder="Company"
                value={exp.company}
                onChange={(e) => handleExperienceChange(index, 'company', e.target.value)}
                required
              />

              <input
                type="text"
                className="form-control"
                placeholder="Year"
                value={exp.year}
                onChange={(e) => handleExperienceChange(index, 'year', e.target.value)}
                required
              />

              <input
                type="text"
                className="form-control"
                placeholder="Designation"
                value={exp.designation}
                onChange={(e) => handleExperienceChange(index, 'designation', e.target.value)}
                required
              />
            </div>
          ))}
          <button type="button" className="btn btn-primary" onClick={handleAddExperience}>
            Add Experience
          </button>
        </div>

        <div className="form-group">
          <label htmlFor="skills">Skills:</label>
          <input
            type="text"
            className="form-control"
            id="skills"
            name="skills"
            value={resume.skills.join(',')}
            onChange={handleSkillChange}
            required
          />
        </div>

        <button type="submit" className="btn btn-primary">Save Resume</button>
      </form>
    </div>
  );
};

export default CreateEditResume;
