import React, { useState } from 'react';

const CreateEditResume = ({ onSave }) => {
  const [name, setName] = useState('');
  const [email, setEmail] = useState('');
  const [address, setAddress] = useState('');
  const [phone, setPhone] = useState('');
  const [education, setEducation] = useState([{ institute: '', year: '', degree: '' }]);
  const [experience, setExperience] = useState([{ company: '', year: '', designation: '' }]);
  const [skills, setSkills] = useState([]);
  const [newSkill, setNewSkill] = useState('');

  const handleEducationChange = (index, event) => {
    const { name, value } = event.target;
    const updatedEducation = [...education];
    updatedEducation[index][name] = value;
    setEducation(updatedEducation);
  };

  const handleExperienceChange = (index, event) => {
    const { name, value } = event.target;
    const updatedExperience = [...experience];
    updatedExperience[index][name] = value;
    setExperience(updatedExperience);
  };

  const handleSkillChange = (event) => {
    setNewSkill(event.target.value);
  };

  const handleAddEducation = () => {
    setEducation([...education, { institute: '', year: '', degree: '' }]);
  };

  const handleAddExperience = () => {
    setExperience([...experience, { company: '', year: '', designation: '' }]);
  };

  const handleAddSkill = () => {
    if (newSkill.trim() !== '') {
      setSkills([...skills, newSkill.trim()]);
      setNewSkill('');
    }
  };

  const handleSave = () => {
    const updatedResume = {
      name: name,
      email: email,
      address: address,
      phone: phone,
      education: education,
      experience: experience,
      skills: skills,
    };
  
    onSave(updatedResume);
  };

  return (
    <div className="container">
      <h1 className="mb-4">Create/Edit Resume</h1>
      <form>
        <div className="form-group">
          <label htmlFor="name">Name</label>
          <input
            type="text"
            className="form-control"
            id="name"
            value={name}
            onChange={(e) => setName(e.target.value)}
          />
        </div>
        <div className="form-group">
          <label htmlFor="email">Email</label>
          <input
            type="email"
            className="form-control"
            id="email"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
          />
        </div>
        <div className="form-group">
          <label htmlFor="address">Address</label>
          <input
            type="text"
            className="form-control"
            id="address"
            value={address}
            onChange={(e) => setAddress(e.target.value)}
          />
        </div>
        <div className="form-group">
          <label htmlFor="phone">Phone</label>
          <input
            type="text"
            className="form-control"
            id="phone"
            value={phone}
            onChange={(e) => setPhone(e.target.value)}
          />
        </div>
        <div className="form-group">
          <label>Education</label>
          {education.map((edu, index) => (
            <div key={index} className="mb-3">
              <input
                type="text"
                className="form-control"
                name="institute"
                placeholder="Institute"
                value={edu.institute}
                onChange={(e) => handleEducationChange(index, e)}
              />
              <input
                type="text"
                className="form-control"
                name="year"
                placeholder="Year"
                value={edu.year}
                onChange={(e) => handleEducationChange(index, e)}
              />
              <input
                type="text"
                className="form-control"
                name="degree"
                placeholder="Degree"
                value={edu.degree}
                onChange={(e) => handleEducationChange(index, e)}
              />
            </div>
          ))}
          <button type="button" className="btn btn-primary" onClick={handleAddEducation}>
            Add Education
          </button>
        </div>
        <div className="form-group">
          <label>Experience</label>
          {experience.map((exp, index) => (
            <div key={index} className="mb-3">
              <input
                type="text"
                className="form-control"
                name="company"
                placeholder="Company"
                value={exp.company}
                onChange={(e) => handleExperienceChange(index, e)}
              />
              <input
                type="text"
                className="form-control"
                name="year"
                placeholder="Year"
                value={exp.year}
                onChange={(e) => handleExperienceChange(index, e)}
              />
              <input
                type="text"
                className="form-control"
                name="designation"
                placeholder="Designation"
                value={exp.designation}
                onChange={(e) => handleExperienceChange(index, e)}
              />
            </div>
          ))}
          <button type="button" className="btn btn-primary" onClick={handleAddExperience}>
            Add Experience
          </button>
        </div>
        <div className="form-group">
          <label htmlFor="skills">Skills</label>
          <div className="input-group mb-3">
            <input
              type="text"
              className="form-control"
              id="skills"
              value={newSkill}
              onChange={handleSkillChange}
            />
            <div className="input-group-append">
              <button type="button" className="btn btn-primary" onClick={handleAddSkill}>
                Add Skill
              </button>
            </div>
          </div>
          <ul className="list-unstyled">
            {skills.map((skill, index) => (
              <li key={index}>{skill}</li>
            ))}
          </ul>
        </div>
        <button type="button" className="btn btn-primary" onClick={handleSave}>
          Save Resume
        </button>
      </form>
    </div>
  );
};

export default CreateEditResume;
