import React, { useState } from 'react';

const CreateEditResume = ({ onSave }) => {
  const [name, setName] = useState('');
  const [email, setEmail] = useState('');
  const [address, setAddress] = useState('');
  const [phone, setPhone] = useState('');
  const [education, setEducation] = useState([{ institute: '', year: '', degree: '' }]);
  const [experience, setExperience] = useState([{ company: '', year: '', designation: '' }]);
  const [skills, setSkills] = useState([]);
  const [newSkill, setNewSkill] = useState('');

  const _handleEducationChange = (index, event) => {
    const { name, value } = event.target;
    const updatedEducation = [...education];
    updatedEducation[index][name] = value;
    setEducation(updatedEducation);
  };

  const _handleExperienceChange = (index, event) => {
    const { name, value } = event.target;
    const updatedExperience = [...experience];
    updatedExperience[index][name] = value;
    setExperience(updatedExperience);
  };

  const _handleSkillChange = (event) => {
    setNewSkill(event.target.value);
  };

  const _handleAddEducation = () => {
    setEducation([...education, { institute: '', year: '', degree: '' }]);
  };

  const _handleAddExperience = () => {
    setExperience([...experience, { company: '', year: '', designation: '' }]);
  };

  const _handleAddSkill = () => {
    if (newSkill.trim() !== '') {
      setSkills([...skills, newSkill.trim()]);
      setNewSkill('');
    }
  };

  const handleSaveResume = () => {
    const updatedResume = {
      name: name,
      email: email,
      address: address,
      phone: phone,
      education: education,
      experience: experience,
      skills: skills,
    };

    onSave(updatedResume);
  };

  return (
    <div className="container">
      <h1 className="mb-4">Create/Edit Resume</h1>
      <form>
        {/* Form fields for Name, Email, Address, and Phone */}
        {/* Education section */}
        {/* Experience section */}
        {/* Skills section */}
        <button type="button" className="btn btn-primary" onClick={handleSaveResume}>
          Save Resume
        </button>
      </form>
    </div>
  );
};

export default CreateEditResume;
